import React from "react";
import NewExpense from "./components/NewExpense/NewExpense";
import Expenses from "./components/Expenses/Expenses";

const App = () => {
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  // return React.createElement(
  //   'div',
  //   {},
  //   React.createElement('h2', {}, "Let's get started!"),
  //   React.createElement(Expenses, { items: expenses })
  // );

  const addExpenseHandler = (expense) => {

    console.log("In App.js");
    console.log(expense);

    let idd = expense.id;
     let titlee = expense.title;
     let amountt = expense.amount;
     let datee = expense.date;



    return expenses.concat(idd, titlee, amountt, datee);



  };

  // const saveDatatoexpenseArray=(expense) => {

  //   let ID= expense.id;
  //   let Title= expense.title;
  //   let Datee= expense.date;
  //   let Amountt= expense.amount;

  //   expenses.concat({id:ID, title:Title, amountt:Amountt, date:Datee});

  // };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />

      <Expenses items={expenses} />
    </div>
  );
};

export default App;
